-(BOOL)isKorean{
    NSArray *languages = [NSLocale preferredLanguages];
    if (languages.count>0) {
       NSString *currentLocaleLanguageCode = languages.firstObject;
        if ([currentLocaleLanguageCode hasPrefix:@"ko"]) {
            return YES;
        }
    }
    return NO;
}

